from typing import List

class Solution:
    def minBuildTime(self, blocks: List[int], split: int) -> int:
        pass

    def dp_slow(self, blocks: List[int], split: int) -> int:
        N = len(blocks)
        blocks.sort(reverse=True)

        if N == 1:
            return blocks[0]

        dp = [[blocks[bi]]*(N+1) for bi in range(N)]

        for bi in range(N-1,-1,-1):
            numleft = N-bi
            for np in range(numleft-1,0,-1):
                if (np-1)*2 >= numleft-1:
                    cs = min((2*np)-numleft, N-1-bi)
                    nbi,nnp = bi+cs, (np-cs)*2
                    minval = max(blocks[bi], split + dp[nbi][nnp]);
                    dp[bi][np] = minval
                else:
                    minval = split + dp[bi][np*2]
                    if np > 1:
                        minval = min(minval, max(blocks[bi], split + dp[bi+1][(np-1)*2]))
                    dp[bi][np] = minval

            if bi < N-1:
                for np in range(3,N+1):
                    dp[bi][np] = min(dp[bi][np],dp[bi+1][np-2])

        return dp[0][1]

